import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OBJExporter } from 'three-stdlib'; // Ensure this is imported correctly

// Define the scene globally
const scene = new THREE.Scene();

// Function to clear the scene before adding new objects
const clearScene = () => {
  console.log("Clearing scene...");
  while (scene.children.length > 0) {
    scene.remove(scene.children[0]);
  }
};


const ThreeJSRenderer = ({ code }) => {
  const mountRef = useRef(null);

  useEffect(() => {
    // Clear the scene before generating new objects
    clearScene();

    // Initialize the camera and renderer
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    mountRef.current.appendChild(renderer.domElement);

    // Add objects to the scene (this is where you generate the 3D models)
    const geometry = new THREE.BoxGeometry();
    const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
    const cube = new THREE.Mesh(geometry, material);
    scene.add(cube); // Add new object to the scene

    // Set camera position
    camera.position.set(0, 5, 10);
    camera.lookAt(new THREE.Vector3(0, 0, 0));

    // Animate the scene
    const animate = () => {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };
    animate();

    // Cleanup on unmount
    return () => {
      if (mountRef.current) {
        mountRef.current.removeChild(renderer.domElement);
      }
    };
  }, [code]); // Depend on code to regenerate models

  // Export function for OBJ
  const exportModelAsObj = () => {
    const exporter = new OBJExporter();
    if (scene.children.length > 0) {
      const objData = exporter.parse(scene);  // Export the scene with objects
      // Trigger download of the .obj file
      const blob = new Blob([objData], { type: 'text/plain' });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = 'model.obj';
      link.click();
    } else {
      console.error('No objects found in the scene for export.');
    }
  };

  return (
    <div>
      <div ref={mountRef}></div>
      <button onClick={exportModelAsObj}>Export as OBJ</button>
    </div>
  );
};

export default ThreeJSRenderer;
