// frontend/src/App.js

import React, { useState } from 'react';
import axios from 'axios';
import ThreeJSRenderer from './components/ThreeJSRenderer';
import CodeDisplay from './components/CodeDisplay'; // Import the new component
import './App.css'; // Ensure this path matches your project structure

function App() {
  const [prompt, setPrompt] = useState('');
  const [code, setCode] = useState('');
  const [error, setError] = useState('');
  const [parseError, setParseError] = useState('');
  const [loading, setLoading] = useState(false); // For loading indicator

  const handleGenerate = async () => {
    if (!prompt.trim()) {
      setError('Please enter a prompt.');
      return;
    }

    setLoading(true);
    setError('');
    setParseError('');
    setCode('');

    try {
      const response = await axios.post(
        '/api/generate-threejs', // Proxy in package.json routes to backend
        { prompt },
        {
          headers: {
            'Content-Type': 'application/json',
            'x-api-key': process.env.REACT_APP_API_KEY, // Ensure this matches the backend API_KEY
          },
        }
      );
      setCode(response.data.code);
    } catch (err) {
      console.error('Error generating code:', err);
      if (err.response && err.response.status === 401) {
        setError('Unauthorized: Invalid API Key.');
      } else {
        setError('An error occurred while generating code.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Three.js IDE</h1>
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Describe your 3D scene..."
          rows="4"
          cols="50"
        />
        <br />
        <button onClick={handleGenerate} disabled={loading}>
          {loading ? 'Generating...' : 'Generate Three.js Code'}
        </button>
        {error && <p className="error">{error}</p>}
        {parseError && <p className="error">{parseError}</p>}
      </header>
      <main>
        {code && <ThreeJSRenderer code={code} setParseError={setParseError} />}
        {code && <CodeDisplay code={code} />} {/* Display the generated code */}
      </main>
    </div>
  );
}

export default App;
