import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OBJExporter } from 'three/examples/jsm/exporters/OBJExporter'; // Import the exporter
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls'; // Import OrbitControls for navigation

// Define the scene globally
const scene = new THREE.Scene();


const ThreeJSRenderer = ({ code }) => {
  const mountRef = useRef(null);
  const controlsRef = useRef(null);
  const meshRef = useRef(null);  // Reference to the generated mesh
  const cameraRef = useRef(null); // Camera reference for controls

  useEffect(() => {
    if (!code) return;

    // Clear any existing scene
    mountRef.current.innerHTML = '';

    // Create a new scene, camera, and renderer
    const scene = new THREE.Scene();

    const camera = new THREE.PerspectiveCamera(
      75,
      mountRef.current.clientWidth / mountRef.current.clientHeight,
      0.1,
      1000
    );
    camera.position.set(2, 2, 2); // Set initial camera position
    cameraRef.current = camera;

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(mountRef.current.clientWidth, mountRef.current.clientHeight);
    mountRef.current.appendChild(renderer.domElement);

    // Add OrbitControls for navigation
    const controls = new OrbitControls(camera, renderer.domElement);
    controlsRef.current = controls;
    controls.update();

    // Log the generated code for debugging
    console.log('Executing Three.js Code:', code);

    // Create a function to execute the generated code
    const executeCode = () => {
      try {
        const func = new Function('THREE', 'scene', 'camera', 'renderer', 'meshRef', code);
        func(THREE, scene, camera, renderer, meshRef);
      } catch (error) {
        console.error('Error executing generated Three.js code:', error);
      }
    };

    executeCode();

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
      controls.update(); // Ensure the controls update
    };

    animate();

    // Cleanup on component unmount
    return () => {
      mountRef.current.innerHTML = '';
    };
  }, [code]);

  // Export function that triggers OBJExporter
  const exportModelAsObj = () => {
    const exporter = new OBJExporter();
    if (scene && scene.children.length > 0) {
      const model = scene.children[0]; // Export the first object in the scene
      const objData = exporter.parse(model);  // Export the model

      // Trigger download of the .obj file
      const blob = new Blob([objData], { type: 'text/plain' });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = 'model.obj';
      link.click();
    } else {
      console.error('No objects found in the scene for export.');
    }
  };

  // Zoom in function
  const handleZoomIn = () => {
    const camera = cameraRef.current;
    if (camera) {
      camera.position.z -= 0.5;
      camera.updateProjectionMatrix();
    }
  };

  // Zoom out function
  const handleZoomOut = () => {
    const camera = cameraRef.current;
    if (camera) {
      camera.position.z += 0.5;
      camera.updateProjectionMatrix();
    }
  };

  // Reset camera function
  const handleResetCamera = () => {
    const camera = cameraRef.current;
    if (camera) {
      camera.position.set(2, 2, 2); // Reset to initial position
      camera.lookAt(0, 0, 0);
      camera.updateProjectionMatrix();
    }
  };



  return (
    <div>
      <div ref={mountRef}></div>
      <button onClick={exportModelAsObj}>Export as OBJ</button>
    </div>
  );
};

export default ThreeJSRenderer;
