import React, { useEffect, useRef, useState } from 'react';
import * as THREE from 'three';
import * as esprima from 'esprima';


const ThreeJSRenderer = ({ code }) => {
  const mountRef = useRef(null);
  const [zoomLevel, setZoomLevel] = useState(5); // Set a more appropriate default zoom level

  useEffect(() => {
    if (!code) return;

    // Clear any existing scene
    mountRef.current.innerHTML = '';

    // Create a new scene, camera, and renderer
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75,
      mountRef.current.clientWidth / mountRef.current.clientHeight,
      0.1,
      1000
    );

    // Set camera position based on zoomLevel
    camera.position.set(0, 0, zoomLevel);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(mountRef.current.clientWidth, mountRef.current.clientHeight);
    mountRef.current.appendChild(renderer.domElement);

    // Log the generated code for debugging
    console.log('Executing Three.js Code:', code);

    // Validate the generated code syntax using Esprima
    try {
      esprima.parseScript(code);
    } catch (parseError) {
      console.error('Generated code has syntax errors:', parseError.message);
      return; // Stop execution if syntax errors are found
    }

    // Create a function to execute the generated code
    const executeCode = () => {
      try {
        console.log('Executing Function Constructor with code:', code);
        const func = new Function('THREE', 'scene', 'camera', 'renderer', code);
        func(THREE, scene, camera, renderer);
        console.log('Code executed successfully.');
      } catch (error) {
        console.error('Error executing generated Three.js code:', error);
      }
    };

    executeCode();

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };

    animate();

    // Cleanup on component unmount
    return () => {
      mountRef.current.innerHTML = '';
    };
  }, [code, zoomLevel]);

  // Handlers for zooming in and out
  const handleZoomIn = () => {
    if (zoomLevel > 1) {
      setZoomLevel((prev) => prev - 1); // Reduce zoom level smoothly
    }
  };
  const handleZoomOut = () => {
    setZoomLevel((prev) => prev + 1); // Increase zoom level smoothly
  };
  const handleResetCamera = () => setZoomLevel(5); // Reset to default zoom level

  return (
    <div>
      <div ref={mountRef} style={{ width: '100%', height: '500px' }} /> {/* Ensures a set height for the viewport */}
      <div style={{ margin: '10px 0' }}>
        <button onClick={handleZoomIn}>üîç Zoom In</button>
        <button onClick={handleZoomOut}>üîç Zoom Out</button>
        <button onClick={handleResetCamera}>üîÑ Reset Camera</button>
      </div>
    </div>
  );
};

export default ThreeJSRenderer;
